/* eslint-disable no-useless-escape */
const validations = (data) => {
  const errors = {};

  var regex = /^[a-zA-Z\s]*$/;
  var regexImage = /^(http(s)?:\/\/)?(www\.)?[a-zA-Z0-9_-]+(\.[a-zA-Z0-9_-]+)+([a-zA-Z0-9_\-\.\?\\/\+=&;%#_]*)?$/;

  if (!regex.test(data.name)) {
    errors.name = "Insert name with letters only!";
  } else if (data.name.trim() === "") {
    return errors.name = "Insert name!";
  } else if (data.name.length > 10) {
    return errors.name = "Insert name with less than 10 letters!";
  }

  if (regexImage.test(data.image)) {
    return errors.image = "Insert name with less than 10 letters!";
  } else if (data.image.trim() === "") {
    return errors.name = "Insert image!";
  }

  var height = data.height.split("-").map((numero) => parseInt(numero.trim()));
  if (
    height.length !== 2 ||
    isNaN(height[0]) || isNaN(height[1]) ||
    height[0] > height[1]
  ) {
    return errors.height = "Insert a min and a max, Eg: 31 - 40";
  } else if (data.height.trim() === "") {
    return errors.height = "Insert a min and a max, Eg: 31 - 40";
  }

  var weight = data.weight.split("-").map((numero) => parseInt(numero.trim()));
  if (
    weight.length !== 2 ||
    isNaN(weight[0]) || isNaN(weight[1]) ||
    weight[0] > weight[1]
  ) {
    return errors.weight = "Insert a min and a max, Eg: 11 - 20";
  } else if (data.weight.trim() === "") {
    return errors.weight = "Insert a min and a max, Eg: 11 - 20";
  }

  var yearsNumeric = parseInt(data.life_span.match(/\d+/));

  if (isNaN(yearsNumeric) || yearsNumeric < 0 || yearsNumeric > 20) {
    return errors.life_span = "Life Years are not valid";
  } else if(data.life_span.trim() === "") {
    return errors.life_span = "insert years of life, eg: 11 Years";
  }

  return errors;
};

export default validations;
